  return (

		<tbody>
      <tr>
        <td>{this.props.post.nb}</td>
        <td>{this.props.post.nom}</td>
        <td>{this.props.post.prenom}</td>
        <td> 
<button className={"btn btn-danger"} onClick={()=>this.props.dispatch({type:'DELETE_POST',id:this.props.post.id})}>X</button></td>        
      </tr>
		</tbody>


  );
 }
}
