import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { items: '', items1: '', text: '', text1: '' };
    this.nomChange = this.nomChange.bind(this);
    this.prenomChange1 = this.prenomChange1.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }
  render(){
    return(
      <div>

        <form onSubmit={this.onSubmit}>
          <label htmlFor="new">
            Nom
          </label>
          <input
            type= 'text'
            onChange={this.nomChange}
            value={this.state.nom}
          />
          <label htmlFor="new">
            Prénom
          </label>
          <input
            id="new-tod"
            onChange={this.prenomChange}
            value={this.state.prenom}
          />

          <button>
            Ajouter 
          </button>
        </form>
      </div>

    );
  }
}
export default App;
