import React, { useState } from 'react'
import Tableau from './composant/Tableau'
import Formulaire from './composant/Formulaire'
import 'bootstrap/dist/css/bootstrap.min.css'
import './composant/Formulaire.css'
import './composant/Tableau.css'


const App = () => {
  const usersData = [
  ]


  const [users, setUsers] = useState(usersData)
  const [count, setCounte] = useState(0);

  const addUser = user => {
    // if(users[users.length-1].id<users.length){
    //   user.id=users.length+1
    //   console.log(user.id);

    // }else{
    //   user.id=users[users.length-1].id+1
    //   console.log(user.id);

    // }
    // console.log(user.id) 


    user.id=count
    setUsers([ ...users, user])
    console.log("uset", user.id) 
    console.log("useers", users[0]) 

  }
  const deleteUser = id => {
    setUsers(users.filter(
      u => u.id !== id
      ))
  }

  return (
    <div className="container">
      <div className="flex-row">
        <div className="flex-large">
          <Formulaire addUser={addUser} setCounte={setCounte} count={count}/>
        </div>
        <div className="flex-large">
          <Tableau users={users}  deleteUser={deleteUser} />
        </div>
      </div>

    </div>
  )
}



export default App
