import React, { Component } from 'react';
import './App.css';

const App = () =>{

  const initialState = {
    title: '',
    available: false,
  }

  const [book, setBook ]= useState(initialState)

  const updateBook = book =>{
    setBook({ title: book.title, available: book.available })
  } 
}
export default App;
