import React, { Component } from 'react'
import {connect} from 'react-redux'

class PostForm extends Component{

    constructor() {
        super()
        this.state = {
            nb: 0,
        }
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({
         nb : this.state.nb + 1
        })

        const nom = this.getNom.value;
        const prenom = this.getPrenom.value;
        const data = {
            id: new Date(),            
            nb : this.setState.nb,
            nom,
            prenom
        }
        this.props.dispatch({
            type:'ADD_POST',
            data
        });
        this.getNom.value = '';
        this.getPrenom.value = '';
        console.log(this.state.nb)
    }
    render(){
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                        <label>Nom</label>
                        <input required type='text' ref={(input)=>this.getNom = input} />
                        <label>Pr√©nom</label>
                        <input required type='text' ref={(input)=>this.getPrenom = input} />
                      <button className={'btn btn-primary'}  >Ajouter</button>

                </form>

            </div>
        )
    }
}
export default connect()(PostForm)
